import React, {useState,useEffect } from 'react';
import {addToCart,removeFromCart} from '../actions/cartActions';
import {useSelector,useDispatch} from 'react-redux';
import {
    Link
  } from "react-router-dom";

function Dropdown(){
    const cart = useSelector(state => state.cart);
    const {cartItems} = cart;

    const dispatch = useDispatch();
    const [open , setOpen] = useState(false);

    const toggle = ()=> {
        console.log('triggered toggle');
        if(open){
          document.querySelector(".dd-wrapper").classList.add("open");
            
        }
        else{
            document.querySelector(".dd-wrapper").classList.remove("open");
        }
        setOpen(!open);
    }

    const handleCart= ()=>{
        console.log('clicked Cart');
        setOpen(!open);
    }
    const handleClose= ()=>{
        console.log('clicked Cart');
        setOpen(!open);
    }
    const removeFromCartHandler = (productId) =>{
        dispatch(removeFromCart(productId));
    }
    useEffect(()=>{

    },[]);
    const checkoutHandler = () =>{
    }
    return <div> {cartItems<1 && !open?  <div onClick={handleCart}>Cart</div>
    
    
    
    :
        
    <div className="dd-wrapper">
                    <div 
            tabIndex={0}
             className="dd-header"
              role="button" 
              onKeyPress={()=>toggle(!open)}
            onClick={()=>toggle(!open)}></div>
                    <div className="dd-header_action">
                <p>{open?<div onClick={handleClose}>Close</div>: 'Cart-'+cartItems.length}</p>
            </div>
        <div className="dd-header">
            <ul className="cart-list-container">
                <li>
                    <h3>
                        Shopping Cart
                    </h3>
                    <div>
                    Price
                    </div>
                </li>
                {
                    cartItems.length === 0 ?
                    <div>
                        cart is empty
                    </div>
                    :
                    cartItems.map(item=> 
                        <li key={item.product}> 
                               <div className="cart-image">
                                    <img src={item.urlImg} className="" alt="product"/>
                                </div>
                                <div className="cart-name">
                                    <div>
                                        <Link to={"/product/"+ item.product}>
                                        {item.name}
                                        </Link>
                                    </div>
                                    <div>
                                        Qty:
                                        <select value={item.qty} onChange={(e)=> dispatch(addToCart(item.product, e.target.value))}>
                                            {[...Array(item.countInStock).keys()].map(x=>
                                                <option key={x+1} value={x+1}>{x+1}</option>)
                                            }
                                        </select>
                                        <button type="button" className="button" onClick={()=>removeFromCartHandler(item.product)}>
                                            Delete
                                        </button>
                                    </div>
                                </div>
                                <div className="cart-price"> 
                                    {item.price}$
                                </div>
                        </li>
                        )
                     }
            </ul>
        </div>
        <div className="cart-action-nav">
            <h3>
                subTotal({cartItems.reduce((a,c)=>a+c.qty,0)} items)
                :
            ${cartItems.reduce((a,c) =>a + c.price* c.qty, 0)}
            </h3>
            <button className= "button primary full-width" onClick={checkoutHandler} disabled={cartItems.length===0}>Proceed to CheckOut</button>
        </div>
    </div> }
    </div>
}

export default Dropdown;
