import React, {useEffect} from 'react';
import { listPayments} from '../actions/paymentActions';
import { useSelector, useDispatch} from 'react-redux';


function StatsScreen(props){

    const dispatch = useDispatch();
    const paymentsList = useSelector(state=> state.paymentList);
    const { payments, loading , error } = paymentsList;

    //fetching data from server
    useEffect(()=>{
        dispatch(listPayments());
        return ()=>{
        };
    },[])

    return loading? <div>loading..</div>:error ? <div>{error}</div>:     <div>
            {payments.length<1 ? <div>no payments yet</div> :
            <div className="p">

                <h2>Top 5 Sel</h2>

                {
               payments.map(item=> 
                        <li key={item.product}> 
                                <div className="item-payment-name">
                                    Product: {item.name}
                                </div>
                                <div className="item-payment-price"> 
                                    Total Money gained: {item.price}$
                                </div>
                                <div className="item-payment-price"> 
                                    Total Qnty sold: {item.amount}
                                </div>                              
                        </li>
                 )}


                <h2>Top 5 unique sel</h2>

                {
                payments.map(item=> 
                        <li key={item.product}> 
                                <div className="item-payment-name">
                                    Product: {item.name}
                                </div>
                                <div className="item-payment-price"> 
                                    Total Money gained: {item.price}$
                                </div>
                                <div className="item-payment-price"> 
                                    Total Qnty sold: {item.amount}
                                </div>                              
                        </li>
                )} 


                <h2>Past 5 days $</h2>
                {
                payments.map(item=> 
                        <li key={item.product}> 
                                <div className="item-payment-name">
                                    Product: {item.name}
                                </div>
                                <div className="item-payment-price"> 
                                    Total Money gained: {item.price}$
                                </div>
                                <div className="item-payment-price"> 
                                    Total Qnty sold: {item.amount}
                                </div>                              
                        </li>
                )} 



            </div>
            
            }
    </div>
}

export default StatsScreen;
