import { confirmAlert } from 'react-confirm-alert';



const addDialog = (id,name,props) => {
  const handleClickedNo = () => {
    alert('clicked no')
    props.history.push("/cart/" + id+"?qty="+ 1)
    onClose()
  }
  const handleClickedYes = () => {
    alert('clicked yes')
    onClose()
  }
  return (
    <div className='add-dialog'>
      <h3>Add item to display</h3>
      <p>item {name} id:{id} has been added to cart</p>
      <div className="add-dialog-buttons">
        <button onClick={handleClickedNo}>Go to Cart</button>
        <button onClick={handleClickedYes}>Continue Shopping..</button>
      </div>
    </div>
  )
}      

confirmAlert({ customUI: addDialog })

export default addDialog;
